import React, { useState, useEffect } from 'react'
import styled from 'styled-components'
import axios from 'axios'
import GameOver from './GameOver';
import Pic1A from '../Image 1_original (1).jpg';
import Pic1B from '../Image 1 Change_final.jpg';
import Pic2A from '../Image 2_original (1).jpg'
import Pic2B from '../Image 2 Change_final.jpg'
import Pic3A from '../Image 3_original.jpg'
import Pic3B from '../Image 3 Change_final.jpg'
import Pic4A from '../Image 4_original.jpg'
import Pic4B from '../Image 4 Change_final.jpg'
import Pic5A from '../Image 5 original.jpg'
import Pic5B from '../Image 5 Change_final.jpg'
import Pic12A from '../Image 12 original.jpg'
import Pic12B from '../Image 12 Change_final.jpg'
import Circle from '../kisspng-disk-green-circle-5af97c364e1283.3175421115262997023198.png'

const QuizWindow = styled.div`
    text-align: center;
    font-size: clamp(20px, 2.5vw, 24px);
    margin-top: 15vh;
`;

const checkPopUp = styled.div`
    top:150px,
    left:150px, // hide div first
    width:100px,
    height:100px,
`;

const Options = styled.div`
    display: flex;
    flex-direction: column;
    width: 70%;
    margin: 2em auto;

    @media screen and (min-width: 1180px) {
        width: 50%;
    }
`;

const Option = styled.button`
    display: block;
    border: 1px solid #616A94;
    border-radius: 15px;
    padding: 15px 30px;
    text-decoration: none;
    color: #616A94;
    background-color: #161A31;
    transition: 0.3s;
    font-size: 1em;
    outline: none;
    user-select: none;
    margin-top: 1em;
    cursor: pointer;

    @media screen and (min-width: 1180px) {
        &:hover {
            color: white;
            background-color: #616A94;
        }
    }
`;

const Question = styled.div`
    width: 70%;
    margin: 0 auto;
`;


const Quiz = () => {

    const [pos, setPos] = useState({
        xoffset: 50,
        yoffset: 50,
        delta: 10,
    });
    const [pos2, setPos2] = useState({
        xoffset: 50,
        yoffset: 50,
        delta: 10,
    });

    const moveTitleToDown = () => {
        setPos(
          { yoffset: pos.yoffset 
            + pos.delta });
      };
      const moveTitleToRight = () => {
        setPos(
          { xoffset: pos.xoffset 
            + pos.delta });
      };
      const moveTitleToLeft = () => {
        setPos(
          { xoffset: pos.xoffset 
            - pos.delta });
      };
      const moveTitleToUp = () => {
        setPos(
          { yoffset: pos.yoffset 
            - pos.delta });
      };

    const [quiz, setQuiz] = useState([]);
    const [number, setNumber] = useState(0);
    const [pts, setPts] = useState([0]);
    const [correct, setCorrect] = useState(false);
    const [wrong, setWrong] = useState(false);
    const [counter, setCounter] = useState(60);
    const [tieTime, setTieTime] = useState(0);
    const [buttonsTime, setButtonsTime] = useState(0);
    const [buttons2Time, setButtons2Time] = useState(0);
    const [BooksTime, setBooksTime] = useState(0);
    const [rightPlantTime, setRightPlantTime] = useState(0);
    const [watchTime, setWatchTime] = useState(0);
    const [bookTime, setBookTime] = useState(0);
    const [showTie, setShowTie] = useState(false);
    const [showButtons, setShowButtons] = useState(false);
    const [showButtons2, setShowButtons2] = useState(false);
    const [showLamp, setShowLamp] = useState(false);
    const [showBooks, setShowBooks] = useState(false);
    const [showRightPlant, setShowRightPlant] = useState(false);
    const [showWatch, setShowWatch] = useState(false);
    const [showBook, setShowBook] = useState(false);
    const [tieCo, setTieCo] = useState({x: 0, y: 0});
    const [tieCo2, setTieCo2] = useState({x: 0, y: 0});
    const [buttonsCo, setButtonsCo] = useState({x: 0, y: 0});
    const [buttonsCo2, setButtonsCo2] = useState({x: 0, y: 0});
    const [buttons2Co, setButtons2Co] = useState({x: 0, y: 0});
    const [buttons2Co2, setButtons2Co2] = useState({x: 0, y: 0});
    const [BooksCo, setBooksCo] = useState({x: 0, y: 0});
    const [BooksCo2, setBooksCo2] = useState({x: 0, y: 0});
    const [rightPlantCo, setRightPlantCo] = useState({x: 0, y: 0});
    const [rightPlantCo2, setRightPlantCo2] = useState({x: 0, y: 0});
    const [watchCo, setWatchCo] = useState({x: 0, y: 0});
    const [watchCo2, setWatchCo2] = useState({x: 0, y: 0});
    const [bookCo, setBookCo] = useState({x: 0, y: 0});
    const [bookCo2, setBookCo2] = useState({x: 0, y: 0});

    useEffect(() => {
        counter > 0 && setTimeout(() => setCounter(counter - 1), 1000);
    }, [counter]);

    const shuffle = (arr) => arr.sort(() => Math.random() - 0.5);

    const setCoordinates = (x,y) => {
        // You don't need whitespace in here, I added it for readability
        // I would recommend using something like EmotionJS for this
            return `position: absolute;   
                    left: ${x}px;         
                    top: ${y}px;`
    }

    const end = () => {
        if (pts[0] == 6){
            setShowBook(false);
            setShowButtons(false);
            setShowRightPlant(false);
            setShowTie(false);
            setShowBooks(false);
            setShowWatch(false);
        }
    }
    const pickAnswer1 = (e) => {

        //let userAnswer = e.target.outerText;

        // if (quiz[number].answer === userAnswer) 
        //console.log("X coordinate: " + e.screenX + " Y coordinate" + e.screenY);
        if ((e.screenX >= 222 && e.screenX <= 251 && e.screenY >= 619 && e.screenY <= 703) || (e.screenX >= 938 && e.screenX <= 967 && e.screenY >= 619 && e.screenY <= 703)){
            console.log("found tie");
            setPts([pts[0] + 1]);
            setCorrect(true);
            setTieTime(60-counter);
            setTieCo({x: 215, y: 500});
            setTieCo2({x: 936, y: 500});
            setShowTie(true);
        }else if ((e.screenX >= 227 && e.screenX <= 260 && e.screenY >= 765 && e.screenY <= 780) || (e.screenX >= 943 && e.screenX <= 976 && e.screenY >= 765 && e.screenY <= 780)){
            console.log("found buttons");
            setPts([pts[0] + 1]);
            setCorrect(true);
            setButtonsTime(60-counter);
            setButtonsCo({x: 217, y: 610});
            setButtonsCo2({x: 933, y: 610});
            setShowButtons(true);
        } else if ((e.screenX >= 496 && e.screenX <= 518 && e.screenY >= 765 && e.screenY <= 780) || (e.screenX >= 1212 && e.screenX <= 1234 && e.screenY >= 765 && e.screenY <= 780)){
            console.log("found second buttons");
            setPts([pts[0] + 1]);
            setCorrect(true);
            setButtons2Time(60-counter);
            setButtons2Co({x: 486, y: 610});
            setButtons2Co2({x: 1202, y: 610});
            setShowButtons2(true);
        } else if ((e.screenX >= 639 && e.screenX <= 669 && e.screenY >= 560 && e.screenY <= 580) || (e.screenX >= 1355 && e.screenX <= 1385 && e.screenY >= 560 && e.screenY <= 580)){
            console.log("found stack of books");
            setPts([pts[0] + 1]);
            setCorrect(true);
            setBooksTime(60-counter);
            setBooksCo({x: 630, y: 410});
            setBooksCo2({x: 1345, y: 410});
            setShowBooks(true);
        } else if ((e.screenX >= 534 && e.screenX <= 603 && e.screenY >= 457 && e.screenY <= 590) || (e.screenX >= 1250 && e.screenX <= 1319 && e.screenY >= 457 && e.screenY <= 590)){
            console.log("found right plant");
            setPts([pts[0] + 1]);
            setCorrect(true);
            setRightPlantTime(60-counter);
            setRightPlantCo({x: 540, y: 330});
            setRightPlantCo2({x: 1255, y: 330});
            setShowRightPlant(true);
        } else if ((e.screenX >= 488 && e.screenX <= 501 && e.screenY >= 704 && e.screenY <= 724) || (e.screenX >= 1204 && e.screenX <= 1217 && e.screenY >= 704 && e.screenY <= 724)){
            console.log("found watch");
            setPts([pts[0] + 1]);
            setCorrect(true);
            setWatchTime(60-counter);
            setWatchCo({x: 480, y: 550});
            setWatchCo2({x: 1200, y: 550});
            setShowWatch(true);
        } else if ((e.screenX >= 168 && e.screenX <= 180 && e.screenY >= 549 && e.screenY <= 572) || (e.screenX >= 884 && e.screenX <= 900 && e.screenY >= 549 && e.screenY <= 572)){
            console.log("found book");
            setPts([pts[0] + 1]);
            setCorrect(true);
            setBookTime(60-counter);
            setBookCo({x: 155, y: 400});
            setBookCo2({x: 875, y: 400});
            setShowBook(true);
        } else if ((e.screenX >= 168 && e.screenX <= 180 && e.screenY >= 549 && e.screenY <= 572) || (e.screenX >= 884 && e.screenX <= 900 && e.screenY >= 549 && e.screenY <= 572)){
            console.log("found lamp");
            setPts([pts[0] + 1]);
            setCorrect(true);
            setLampTime(60-counter);
            setLampCo({x: 155, y: 400});
            setLampCo2({x: 875, y: 400});
            setShowLamp(true);
        } else {
            setWrong(true);
        }
        console.log("x: " + e.screenX + " y: " + e.screenY);
        setTimeout(() => {setCorrect(false)}, 1000);
        setTimeout(() => {setWrong(false)}, 1000);

    }

    const pickAnswer2 = (e) => {

        //let userAnswer = e.target.outerText;

        // if (quiz[number].answer === userAnswer) 
        setPts(pts + 1);
    }

    const regulateSlide = (num) => {
        if (num == 1){

        }
        setCounter(10);
        return true;
    }

    useEffect(() => {
        // axios.get('https://opentdb.com/api.php?amount=5&category=18&difficulty=easy&type=multiple')
        //     .then(res => {
        //         setQuiz(res.data.results.map(item => (

        //             {
        //                 question: item.question,
        //                 options: shuffle([...item.incorrect_answers, item.correct_answer]),
        //                 answer: item.correct_answer
        //             }

        //         )));
        //     })
        //     .catch(err => console.error(err))


    }, []);


    return (
        // <QuizWindow>
        //     { quiz[number] &&

        //         <>
        //             <Question dangerouslySetInnerHTML={{ __html: quiz[number].question }}></Question>

        //             <Options>
        //                 {quiz[number].options.map((item, index) => (
        //                     <Option key={index} dangerouslySetInnerHTML={{ __html: item }} onClick={pickAnswer}></Option>
        //                 ))}
        //             </Options>
        //         </>

        //     }
        //     {
        //         number === 5 && <GameOver pts={pts} />
        //     }
        // </QuizWindow>
        <QuizWindow>

            {pts[0] <= 7 && counter > 0 && 
                <>
                    <div> Image 1</div>
                    <h1> Spot the Differences! </h1>
                    <img src={Pic1A} alt="Picture 1A" height="50%" width="50%" onClick={pickAnswer1} />
                    <img src={Pic1B} alt="Picture 1B" height="50%" width="50%" onClick={pickAnswer1}/>
                </>
            } 
            {/* {((pts > 5 && pts <=10) || (counter < 10 && counter > 0)) &&
                <>
                    <div> Timer: {counter}</div>
                    <h1> Spot the Differences! (2/10) </h1>
                    <img src={Pic2A} alt="Picture 2A" height="50%" width="50%" onClick={pickAnswer2} />
                    <img src={Pic2B} alt="Picture 2B" height="50%" width="50%" onClick={pickAnswer2}/>
                </>
            }   */}
            { (pts[0] == 8 || counter == 0) &&  
                <>
                <GameOver pts = {pts} />
                <h1> </h1>
                {tieTime != 0 ? 
                    <h6> Time to find tie: {tieTime} seconds</h6>
                : 
                    <h6> Time to find tie: Not found</h6>
                }
                {buttonsTime != 0 ? 
                    <h6> Time to find buttons: {buttonsTime} seconds</h6>
                :
                    <h6> Time to find buttons: Not found</h6>
                }
                {buttons2Time != 0 ? 
                    <h6> Time to find second buttons: {buttons2Time} seconds</h6>
                :
                    <h6> Time to find second buttons: Not found</h6>
                }
                {BooksTime != 0 ? 
                    <h6> Time to find book stack: {BooksTime} seconds</h6>
                :
                    <h6> Time to find book stack: Not found</h6>
                }
                {rightPlantTime != 0 ? 
                    <h6> Time to find right plant: {rightPlantTime} seconds</h6>
                :
                    <h6> Time to find right plant: Not found</h6>
                }
                {watchTime != 0 ? 
                    <h6> Time to find watch: {watchTime} seconds</h6>
                :
                    <h6> Time to find watch: Not found</h6>
                }
                {bookTime != 0 ? 
                    <h6> Time to find book: {bookTime} seconds</h6>
                :
                    <h6> Time to find book: Not found</h6>
                }
                {lampTime != 0 ? 
                    <h6> Time to find lamp: {lampTime} seconds</h6>
                :
                    <h6> Time to find lamp: Not found</h6>
                }
                </>
            }
            {correct &&
                <div>
                    ✅ Correct!
                </div>

            }
            {wrong &&
                <div>
                     ❌ Keep looking!
                </div>
            }
            {showButtons &&
                <div>
                <img alt="Pic1" height="50" width="50" src={Circle} style={{
                    position: "absolute",
                    left: `${buttonsCo.x}px`,
                    top: `${buttonsCo.y}px`, }}/>
                <img height="50" width="50" src={Circle} style={{
                    position: "absolute",
                    left: `${buttonsCo2.x}px`,
                    top: `${buttonsCo2.y}px`, }}/>
                </div>
            }
            {showButtons2 &&
                <div>
                <img alt="Pic1" height="50" width="50" src={Circle} style={{
                    position: "absolute",
                    left: `${buttons2Co.x}px`,
                    top: `${buttons2Co.y}px`, }}/>
                <img height="50" width="50" src={Circle} style={{
                    position: "absolute",
                    left: `${buttons2Co2.x}px`,
                    top: `${buttons2Co2.y}px`, }}/>
                </div>
            }
            {showTie &&
                <div>
                <img alt="Pic2" height="50" width="50" src={Circle} style={{
                    position: "absolute",
                    left: `${tieCo.x}px`,
                    top: `${tieCo.y}px`, }}/>
                <img height="50" width="50" src={Circle} style={{
                    position: "absolute",
                    left: `${tieCo2.x}px`,
                    top: `${tieCo2.y}px`, }}/>
                </div>
            }
            {showBooks &&
                <div>
                <img height="50" width="50" src={Circle} style={{
                    position: "absolute",
                    left: `${BooksCo.x}px`,
                    top: `${BooksCo.y}px`, }}/>
                <img height="50" width="50" src={Circle} style={{
                    position: "absolute",
                    left: `${BooksCo2.x}px`,
                    top: `${BooksCo2.y}px`, }}/>
                </div>
            }
            {showRightPlant &&
                <div>
                <img height="50" width="50" src={Circle} style={{
                    position: "absolute",
                    left: `${rightPlantCo.x}px`,
                    top: `${rightPlantCo.y}px`, }}/>
                <img height="50" width="50" src={Circle} style={{
                    position: "absolute",
                    left: `${rightPlantCo2.x}px`,
                    top: `${rightPlantCo2.y}px`, }}/>
                </div>
            }
            {showBook &&
                <div>
                <img height="50" width="50" src={Circle} style={{
                    position: "absolute",
                    left: `${bookCo.x}px`,
                    top: `${bookCo.y}px`, }}/>
                <img height="50" width="50" src={Circle} style={{
                    position: "absolute",
                    left: `${bookCo2.x}px`,
                    top: `${bookCo2.y}px`, }}/>
                </div>
            }
            {showWatch &&
                <div>
                <img height="50" width="50" src={Circle} style={{
                    position: "absolute",
                    left: `${watchCo.x}px`,
                    top: `${watchCo.y}px`, }}/>
                <img height="50" width="50" src={Circle} style={{
                    position: "absolute",
                    left: `${watchCo2.x}px`,
                    top: `${watchCo2.y}px`, }}/>
                </div>
            }
        </QuizWindow>

    )
}

export default Quiz

