            setPts([pts[0] + 1]);
            setCorrect(true);
        }
        if ((e.screenX >= 300 && e.screenX <= 309 && e.screenY >= 562 && e.screenY <= 574) || (e.screenX >= 743 && e.screenX <= 752 && e.screenY >= 562 && e.screenY <= 574)){
            console.log("found watch");
            setPts([pts[0] + 1]);
            setCorrect(true);
        }
        if ((e.screenX >= 104 && e.screenX <= 111 && e.screenY >= 467 && e.screenY <= 480) || (e.screenX >= 547 && e.screenX <= 554 && e.screenY >= 467 && e.screenY <= 480)){
            console.log("found book");
            setPts([pts[0] + 1]);
            setCorrect(true);
        }
        console.log(pts[0]);
        setCorrect(false);
    }

    const pickAnswer2 = (e) => {

        //let userAnswer = e.target.outerText;

        // if (quiz[number].answer === userAnswer) 
