        // </QuizWindow>
        <QuizWindow>
            {correct &&
                <div>
                    Correct
                </div>
            }
            {
                <>

                </>
            }
            {pts[0] <= 5 && counter > 0 && 
                <>
                    <div> Timer: {counter}</div>
                    <h1> Spot the Differences! (1/10) </h1>
                    <img src={Pic1A} alt="Picture 1A" height="50%" width="50%" onClick={pickAnswer1} />
                    <img src={Pic1B} alt="Picture 1B" height="50%" width="50%" onClick={pickAnswer1}/>
                </>
            } 
            {/* {((pts > 5 && pts <=10) || (counter < 10 && counter > 0)) &&
