            {pts <= 5 && counter >= 10 && 
                <>
                    <div> Timer: {counter}</div>
                    <h1> Spot the Differences! (1/10) </h1>
                    <img src={Pic1A} alt="Picture 1A" height="50%" width="50%" onClick={pickAnswer} />
